Python<pre><code>from sympy import *

def steffensen(x):
    def f(x):
        return acos(-(1/(1 + exp(-2*x))))

    s = f(x)
    t = f(s)
    update_x = x - (s - x)**2 / (t - 2 * s + x)
    print(update_x)
    return steffensen(update_x) if abs(update_x - x) &gt; 0.000005 else update_x
